// [snowpack] add styles to the page (skip if no document exists)
if (typeof document !== 'undefined') {
  const code = ".square {\n  background-color: var(--bg-color);\n  display: flex;\n  position: relative;\n  width: 100%;\n  aspect-ratio: 1/1;\n  justify-content: center;\n  align-items: center;\n  text-decoration: none;\n  color: black;\n  border: 0.5rem solid black;\n  cursor: pointer;\n}\n.square.winner:not(.o)::after, .square.winner::before {\n  background-color: green !important;\n}\n.square.solid {\n  cursor: no-drop;\n}\n.square.x {\n  position: relative;\n}\n.square.x.ghost::after, .square.x.ghost::before {\n  background-color: gray;\n}\n.square.x.solid::after, .square.x.solid::before {\n  background-color: black;\n}\n.square.x::after, .square.x::before {\n  content: \"\";\n  display: block;\n  position: absolute;\n  width: 20%;\n  aspect-ratio: 1/4;\n}\n.square.x::after {\n  transform: rotate(45deg);\n}\n.square.x::before {\n  transform: rotate(-45deg);\n}\n.square.o {\n  position: relative;\n}\n.square.o.ghost::after {\n  background-color: var(--bg-color);\n}\n.square.o.ghost::before {\n  background-color: gray;\n}\n.square.o.solid::after {\n  background-color: var(--bg-color);\n}\n.square.o.solid::before {\n  background-color: black;\n}\n.square.o::after, .square.o::before {\n  content: \"\";\n  display: block;\n  position: absolute;\n  width: 72%;\n  aspect-ratio: 1/1;\n  border-radius: 50%;\n}\n.square.o::after {\n  transform: scale(0.5);\n}\n.square:nth-child(1), .square:nth-child(2), .square:nth-child(3) {\n  border-top: none;\n}\n.square:nth-child(1), .square:nth-child(4), .square:nth-child(7) {\n  border-left: none;\n}\n.square:nth-child(7), .square:nth-child(8), .square:nth-child(9) {\n  border-bottom: none;\n}\n.square:nth-child(3), .square:nth-child(6), .square:nth-child(9) {\n  border-right: none;\n}";

  const styleEl = document.createElement("style");
  const codeEl = document.createTextNode(code);
  styleEl.type = 'text/css';
  styleEl.appendChild(codeEl);
  document.head.appendChild(styleEl);
}